mapper.select:
select id, btype, status, groupName, name, birth, phone, email, zipcode, addr1, addr2, SunLightYn, modelname, UseYn, DATE_FORMAT(CreatedAt, '%Y.%m.%d') CreatedAt, DATE_FORMAT(UpdatedAt, '%Y.%m.%d') UpdatedAt
from business
where id = #{id}

mapper.update:
update business
set
      BType = #{btype}
    , Status = #{status}
    , GroupName = #{groupName}
    , Name = #{name}
    , Birth = #{birth}
    , Phone = #{phone}
    , Email = #{email}
    , Zipcode = #{zipcode}
    , Addr1 = #{addr1}
    , Addr2 = #{addr2}
    , SunLightYn = #{sunLightYn}
    , ModelName = #{modelName}
    , UpdatedAt = CURRENT_TIMESTAMP()
where id = #{id}

update business
set
      Status = #{status}
    , UpdatedAt = CURRENT_TIMESTAMP()
where id = #{id}
