mapper.select:
select id, btype, groupName, addr1, addr2, businessSummary, packageCompose, MarkYn, TagYn1, TagYn2, TagYn3, TagYn4, TagYn5, TagYn6, TagYn7, TagYn8, TagYn9, UseYn, DATE_FORMAT(CreatedAt, '%Y.%m.%d') CreatedAt, DATE_FORMAT(UpdatedAt, '%Y.%m.%d') UpdatedAt
from construction_case
where id = #{id}

mapper.update:
update construction_case
set
      BType = #{bType}
    , GroupName = #{groupName}
    , Addr1 = #{addr1}
    , Addr2 = #{addr2}
    , BusinessSummary = #{businessSummary}
    , PackageCompose = #{packageCompose}
    , ImageFile = #{imageFile}
    , ImageUrl = #{imageUrl}
    , MarkYn = #{markYn}
    , TagYn1 = #{tagYn1}
    , TagYn2 = #{tagYn2}
    , TagYn3 = #{tagYn3}
    , TagYn4 = #{tagYn4}
    , TagYn5 = #{tagYn5}
    , TagYn6 = #{tagYn6}
    , TagYn7 = #{tagYn7}
    , TagYn8 = #{tagYn8}
    , TagYn9 = #{tagYn9}
    , UpdatedAt = CURRENT_TIMESTAMP()
where id = #{id}
