mapper.select:
select id, title, description, ImageFile, ImageUrl, LinkUrl, Target, UseYn, DATE_FORMAT(CreatedAt, '%y.%m.%d') CreatedAt, DATE_FORMAT(UpdatedAt, '%Y.%m.%d') UpdatedAt
from faq
where id = #{id}

mapper.update:
update faq
set
      title = #{title}
    , description = #{description}
    , ImageFile = #{ImageFile}
    , ImageUrl = #{ImageUrl}
    , LinkUrl = #{LinkUrl}
    , Target = #{Target}
    , UpdatedAt = CURRENT_TIMESTAMP()
where id = #{id}
