mapper.select:
select id, qTitle, aTitle, description, UseYn, DATE_FORMAT(CreatedAt, '%Y.%m.%d') CreatedAt, DATE_FORMAT(UpdatedAt, '%Y.%m.%d') UpdatedAt
from faq
where id = #{id}

mapper.update:
update faq
set
      qTitle = #{qTitle}
    , aTitle = #{aTitle}
    , description = #{description}
    , UpdatedAt = CURRENT_TIMESTAMP()
where id = #{id}
