mapper.select:
select id, title, description, imagefile, imageurl, useyn, DATE_FORMAT(createdat, '%y.%m.%d') createdat, DATE_FORMAT(updatedat, '%Y.%m.%d') updatedat
from notice
where id = #{id}

mapper.update:
update notice
set
      title = #{title}
    , description = #{description}
    , imagefile = #{imagefile}
    , imageurl = #{imageurl}
    , updatedat = CURRENT_TIMESTAMP()
where id = #{id}
